import React, { useState } from 'react';
import { updateIssue } from '../../actions/issues';
import { useDispatch, useSelector } from 'react-redux';
import { updateStickyMessage } from '../../actions/stickyMessages';

const UpdateForm = ({ stickyMessage, toggleEditMode }) => {

  const [formData, setFormData] = useState({
    title: stickyMessage.title,
    body: stickyMessage.body
  });

  const handleSubmit = (e) =>  {
    e.preventDefault();
    toggleEditMode();
    console.log(formData)
    //dispatch(updateStickyMessage(org._id, project._id, issue._id, formData));
  }

  return (
    <div className="card">
      <form onSubmit={handleSubmit} >
        <div className="row card-header">
          <div className="col-7">
                <input className="form-control" value={formData.title} />
          </div>
          <div className="col-4">
            <input type="submit" className="btn btn-success" value="Save" />
          </div>
        </div>



          <div className="card-body">
            <textarea className="form-control" value={stickyMessage.body}></textarea>
          </div>
      </form>
    </div>
  )
}

export default UpdateForm
