import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getUser } from '../../actions/users';

const Account = ({ history, match }) => {

  const user = useSelector((state) => state.user);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getUser(match.params.id));
  }, [user]);



  const [formData, setFormData] = useState ({
    email: "",
    username: "",
    password: ""
  });


  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(formData);
  }

  return (
    <div>
      <h1>Edit Profile</h1>
      <form onSubmit={handleSubmit}>
        <div class="form-group">
          <label htmlFor="email">Email address</label>
          <input onChange={(e) => setFormData({ ...formData, email: e.target.value})} value={user.email} type="email" class="form-control" id="email" aria-describedby="emailHelp" placeholder="Enter email"/>
          <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
        </div>
        <div class="form-group">
          <label htmlFor="username">Username</label>
          <input onChange={(e) => setFormData({ ...formData, username: e.target.value})} value={user.username} type="text" class="form-control" id="username" aria-describedby="usernameHelp" placeholder="Enter email"/>
        </div>
        <div class="form-group">
          <label htmlFor="password">Password</label>
          <input onChange={(e) => setFormData({ ...formData, password: e.target.value})} value={user.password} type="password" class="form-control" id="password" placeholder="Password" />
        </div>
        <button type="submit" class="btn btn-primary">Sign Up</button>
      </form>
    </div>
  );
}


export default Account;
