const defaultState = JSON.parse(localStorage.getItem('auth'));


export default (auth = defaultState, action ) => {
  switch (action.type) {
    case "LOG_IN":
      return action.payload;
    case "LOG_OUT":
      return null;
    default:
      return auth;
  }

}
