import React, {useState } from 'react';
import { useDispatch } from 'react-redux';
import { createPost } from '../../../actions/posts';

const Form = () => {

  const [formData, setFormData] = useState({
    title: "",
    body: ""
  })

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(formData);
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div class="form-group">
          <label htmlFor="title">Title</label>
          <input onChange={(e) => setFormData({ ...formData, title: e.target.value})} value={formData.title} type="email" class="form-control" id="email" aria-describedby="title" placeholder="Enter email"/>
          <small id="title" class="form-text text-muted">We'll never share your email with anyone else.</small>
        </div>
        <div class="form-group">
          <label for="body">Body</label>
          <textarea 
          onChange={(e) => setFormData({ ...formData, body: e.target.value})}
          value={formData.body} class="form-control" id="body" rows="3"></textarea>
        </div>
        <button type="submit" class="btn btn-primary">Create Post</button>
      </form>
    </div>
  )
}

export default Form;
