//import { createComment } from '../../actions/comments';
import { useDispatch } from 'react-redux';

const Form = ({ issue }) => {
  const dispatch = useDispatch();
  const [formData, setFormData] = useState("");

  const handleSubmit = (e) =>{
    e.preventDefault();
    console.log(formData);
    //dispatch(createComment(formData, "issue", issue._id ))
  }
  return (
    <div className="comment-form">
      <form onSubmit={handleSubmit}>
      <textarea onChange={(e) => setFormData(e.target.value) } placeholder="Write a new comment..." className="mt-3 form-control"></textarea>
      <input className="btn btn-success btn-block mt-2" type="submit" value="Post" />
      </form>
    </div>
  )
}

export default Form
