import React from 'react'

const IssueListItem = ({issue}) => {

  const renderButton = () => {
    let buttonClass;
    let priorityText;
    switch(issue.priority) {
      case 1: 
        buttonClass = "primary";
        priorityText = "Low";
        break;
      case 2:
        buttonClass = "success";
        priorityText = "Medium";
        break;
      case 3:
        buttonClass = "warning";
        priorityText = "High";
        break;
      case 4: 
        buttonClass =  "danger";
        priorityText = "Urgent";
        break;
    }
    return (
      <button className={`btn btn-${buttonClass} mr-4`}>{priorityText}</button>
    )
  }
  return (
      <div className="card">
        <div className="card-header" id="headingOne">
          <h5 className="mb-0">
            <button className="btn btn-link" data-toggle="collapse" data-target={`#collapse-${issue._id}`} aria-expanded="true" aria-controls={`collapse-${issue._id}`}>
            {renderButton()} {issue.title}
            </button>
          </h5>
        </div>

        <div id={`collapse-${issue._id}`} className="collapse show" aria-labelledby="headingOne" data-parent="#accordion-issues">
          <div className="card-body">
            {issue.body}
          </div>
        </div>
    </div>
  )
}

export default IssueListItem;
