import React, { useState } from 'react';
import { updateIssue } from '../../actions/issues';
import { useDispatch, useSelector } from 'react-redux';
import { updateStickyMessage } from '../../actions/stickyMessages';

const UpdateForm = ({ stickyMessage, toggleEditMode }) => {

  const [formData, setFormData] = useState({
    title: stickyMessage.title,
    body: stickyMessage.body
  });

  const handleSubmit = (e) =>  {
    e.preventDefault();
    toggleEditMode();
    console.log(formData)
    //dispatch(updateStickyMessage(org._id, project._id, issue._id, formData));
  }

  return (
    <div className="card">
      <form>
        <div className="card-header" id="headingOne">
            <input className="form-control" value={formData.title} />
            <button className="btn btn-link" data-toggle="collapse" data-target={`#collapse-${stickyMessage._id}`} aria-expanded="true" aria-controls={`collapse-${stickyMessage._id}`}>
                  {stickyMessage.title} 
            </button>

        </div>

        <div id={`collapse-${stickyMessage._id}`} className="collapse" aria-labelledby="headingOne" data-parent="#sticky-message-accordion">
          <div className="card-body">
            {stickyMessage.body}
          </div>
        </div>
      </form>
    </div>
  )
}

export default UpdateForm
