import { LOG_IN } from '../types/index';

export default (flashMessages = [], action ) => {
  switch (action.type) {
    case LOG_IN:
      return [...flashMessages, {type: 'success', message: "You are now logged in."}]
    default:
      return flashMessages;
  }

}
