import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getUser } from '../../actions/users';

const Account = ({ history, match }) => {

  const user = useSelector((state) => state.user);
  const auth = useSelector((state) => state.auth);
  const dispatch = useDispatch();

  useEffect(() => {
    console.log("GET USER IN PAGE")
    dispatch(getUser(auth.user._id));
  }, []);



  const [formData, setFormData] = useState ({
    email: formData.user.email,
    username: formData.user.username
  });


  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(formData);
  }

  return (
    <div className="user-page">
      {user && (
        <div>
          <h1>Edit Profile</h1>
          <form onSubmit={handleSubmit}>
            <div class="form-group">
              <label htmlFor="email">Email address</label>
              <input onChange={(e) => setFormData({ ...formData, email: e.target.value})} value={formData.email} type="email" class="form-control" id="email" aria-describedby="emailHelp" placeholder="Enter email"/>
              <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
            </div>
            <div class="form-group">
              <label htmlFor="username">Username</label>
              <input onChange={(e) => setFormData({ ...formData, username: e.target.value})} value={formData.username} type="text" class="form-control" id="username" aria-describedby="usernameHelp" placeholder="Enter email"/>
            </div>
            <button type="submit" class="btn btn-primary">Save</button>
          </form>
        </div> 
      )}
    </div>
  );
}


export default Account;
